import React, { Component } from "react";
import Person from "./Persons";

//style
import "./style.css";

class App extends Component {
  state = {
    name: "Milos",
  };
  changeStateName = () => {
    this.setState({
      name: "Suza",
    });
  };
  render() {
    return (
      <div>
        <h3>{this.state.name}</h3>
        <button onClick={this.changeStateName}>Change name</button>
      </div>
    );
  }
}
export default App;
